import React, { useState, useCallback } from "react";
import { Card, Avatar, List } from 'antd';
import { RetweetOutlined, HeartOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design/icons';
import CommentForm from './CommentForm';

const { Meta } = Card;

const PostCard = ({ post }) => {
    const [commentFormOpened, setCommentFormOpened] = useState(false);

    const onToggleComment = useCallback(() => {
        setCommentFormOpened((prev) => !prev);
    }, []);

    // console.log("post : ", post);

    return (
        <>
            <Card
                style={{ width: "100%" }}
                cover={post.Images[0] && <img alt="example" src={post.Images[0].src} />}
                actions={[
                    <RetweetOutlined key="retweet" />,
                    <HeartOutlined key="heart" onClick="" />,
                    // <MessageOutlined key="message" onClick="" />,
                    <MessageOutlined key="message" onClick={onToggleComment} />,
                    <EllipsisOutlined />
                ]}
            >
                <Meta
                    avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                    description={post.content}
                />
            </Card>

            {commentFormOpened && (
                <>
                    <CommentForm />

                </>
            )}

        </>
    );
};


export default PostCard;
