import { all, fork, call, put } from "redux=-saga/effects";

function loginAPI() {
    return axios.post('api/login');
}

function* login() {
    const result = yield call(loginAPI);
}

function* watchLogin() {
    yield take("LOG_IN_REQUEST", login);
}

export default function* rootSaga() {
    yield all([
        fork(watchLogin),
    ]);
}
