export const initialState = {
    isLoggedIn: false,
    me: null,
    signUpData: {},
    loginData: {},
};


const reducer = (state = initialState, action) => {
    switch (action.type) {
        case "LOG_IN_REQUEST":
            return {
                ...state,
                isLoggingIn: true,
            };

        case "LOG_IN_SUCCESS":
            return {
                ...state,
                isLoggingIn: false,
                isLoggedIn: true,
                me: action.data,
            };

        case "LOG_IN_FAILURE":
            return {
                ...state,
                isLoggingIn: false,
            };

        case "LOG_OUT_REQUEST":
            return {
                ...state,
                isLoggingOut: true,
            };

        case "LOG_OUT_SUCCESS":
            return {
                ...state,
                isLoggingOut: false,
                isLoggedIn: false,
                me: null,
            };

        case "LOG_OUT_FAILURE":
            return {
                ...state,
                isLoggingOut: false,
            };


        default:
            return state;
    }
};

export default reducer;
