import { all, delay, fork, put, takeLatest } from 'redux-saga/effects';





function* watchLogIn() {
    yield takeLatest(LOG_IN_REQUEST, logIn);
}


export default function* userSaga() {
    yield all([
        fork(watchLogIn),
    ]);
}
